// src/components/Chatbot/Chatbot.js
import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import { FaRobot, FaTimes } from 'react-icons/fa';

function Chatbot() {
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 20, y: 20 });
  const [startPosition, setStartPosition] = useState({ x: 0, y: 0 });
  const chatRef = useRef(null);
  const windowRef = useRef(null);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartPosition({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      const newX = e.clientX - startPosition.x;
      const newY = e.clientY - startPosition.y;
      setPosition({ x: newX, y: newY });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    } else {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    }

    // Cleanup on unmount
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  useEffect(() => {
    if (windowRef.current && chatRef.current) {
      const { innerWidth, innerHeight } = window;
      const chatWindow = windowRef.current;
      const { offsetWidth: windowWidth, offsetHeight: windowHeight } = chatWindow;
      const chatIcon = chatRef.current;

      let chatX = position.x - windowWidth / 2 + chatIcon.offsetWidth / 2;
      let chatY = position.y - windowHeight - 20; // 20px for spacing

      // Adjust positions if out of viewport
      if (chatX < 0) chatX = 0;
      if (chatX + windowWidth > innerWidth) chatX = innerWidth - windowWidth;
      if (chatY < 0) chatY = 0;
      if (chatY + windowHeight > innerHeight) chatY = innerHeight - windowHeight;

      windowRef.current.style.left = `${chatX}px`;
      windowRef.current.style.top = `${chatY}px`;
    }
  }, [position]);

  return (
    <>
      <div
        className="chatbot-icon"
        ref={chatRef}
        style={{ left: `${position.x}px`, top: `${position.y}px` }}
        onMouseDown={handleMouseDown}
      >
        <FaRobot size={30} />
      </div>

      <div className="chat-window" ref={windowRef}>
        <div className="chat-header">
          <h4>Chat with us!</h4>
          <div className="chat-controls">
            <FaTimes onClick={() => setIsDragging(false)} />
          </div>
        </div>
        <div className="chat-body">
          <p>Welcome to our chatbot! How can we help you today?</p>
          {/* You can add an input and buttons here for actual chat functionality */}
        </div>
      </div>
    </>
  );
}

export default Chatbot;
