// src/components/Chatbot/Chatbot.js
import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import { FaRobot, FaTimes } from 'react-icons/fa';

function Chatbot() {
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 20, y: 20 });
  const [startPosition, setStartPosition] = useState({ x: 0, y: 0 });
  const [messages, setMessages] = useState([]);
  const chatRef = useRef(null);
  const windowRef = useRef(null);
  const inputRef = useRef(null);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartPosition({
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      const newX = e.clientX - startPosition.x;
      const newY = e.clientY - startPosition.y;
      setPosition({ x: newX, y: newY });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleSendMessage = (e) => {
    if (e.key === 'Enter' && e.target.value.trim()) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { type: 'user', text: e.target.value },
        { type: 'bot', text: "I can't talk right now, come back later." },
      ]);
      e.target.value = '';
    }
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    } else {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  useEffect(() => {
    if (windowRef.current && chatRef.current) {
      const chatIcon = chatRef.current;
      const chatWindow = windowRef.current;

      const chatX = position.x + chatIcon.offsetWidth;
      const chatY = position.y - chatWindow.offsetHeight;

      windowRef.current.style.left = `${chatX}px`;
      windowRef.current.style.top = `${chatY}px`;
    }
  }, [position]);

  return (
    <>
      <div
        className="chatbot-icon"
        ref={chatRef}
        style={{ left: `${position.x}px`, top: `${position.y}px` }}
        onMouseDown={handleMouseDown}
      >
        <FaRobot size={30} />
      </div>

      <div className="chat-window" ref={windowRef}>
        <div className="chat-header">
          <h4>Chat with us!</h4>
          <div className="chat-controls">
            <FaTimes onClick={() => setIsDragging(false)} />
          </div>
        </div>
        <div className="chat-body">
          {messages.map((msg, index) => (
            <p key={index} className={msg.type === 'bot' ? 'bot-message' : 'user-message'}>
              {msg.text}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            ref={inputRef}
            onKeyPress={handleSendMessage}
            placeholder="Type a message..."
          />
        </div>
      </div>
    </>
  );
}

export default Chatbot;
