// src/components/Services/Services.js
import React, { useState } from 'react';
import './Services.css';
import { FaLightbulb, FaPencilAlt, FaPalette, FaComments, FaBook, FaDesktop, FaRedo } from 'react-icons/fa';

function Services() {
  const [expandedCard, setExpandedCard] = useState(null);

  const handleCardClick = (index) => {
    setExpandedCard(expandedCard === index ? null : index); // Toggle the expanded state
  };

  const servicesList = [
    {
      icon: <FaLightbulb />,
      title: 'Brand Strategy Development',
      description: 'Craft a comprehensive strategy to define your brand\'s identity, values, and market positioning.',
      details: 'We dive deep into the core of your brand, exploring market trends, customer personas, and competitive analysis. This service is like being the detective and superhero your brand needs!',
    },
    {
      icon: <FaPencilAlt />,
      title: 'Logo Design',
      description: 'Create a unique and memorable logo that represents your brand\'s identity and resonates with your audience.',
      details: 'Our creative team sketches, doodles, and brainstorms to craft a logo that makes your brand unforgettable. It’s like a fashion makeover, but for your logo!',
    },
    {
      icon: <FaPalette />,
      title: 'Brand Identity Development',
      description: 'Develop a cohesive brand identity that includes visual elements and tone of voice, ensuring consistent communication.',
      details: 'We design everything from colors to typography, ensuring your brand looks sharp and speaks your language. Think of it as a style guide for your brand’s personality.',
    },
    {
      icon: <FaComments />,
      title: 'Brand Messaging',
      description: 'Formulate clear and compelling messaging that communicates your brand\'s values and offerings effectively.',
      details: 'We craft messages that are not only clear but also witty and memorable. It’s like having a stand-up comedian and a poet write your brand’s story!',
    },
    {
      icon: <FaBook />,
      title: 'Brand Guidelines',
      description: 'Establish guidelines for consistent branding across all media, including logo usage, color schemes, and typography.',
      details: 'We create a rulebook that keeps everyone in line—designers, marketers, even your mom! No more guessing how to use your logo or which font to use.',
    },
    {
      icon: <FaDesktop />,
      title: 'Brand Collateral Design',
      description: 'Design marketing materials such as brochures, business cards, and digital assets that reflect your brand\'s identity.',
      details: 'From snazzy business cards to eye-catching brochures, we design all the goodies that make your brand stand out. It’s like giving your brand a wardrobe upgrade!',
    },
    {
      icon: <FaRedo />,
      title: 'Brand Refresh or Rebranding',
      description: 'Revitalize or completely overhaul your brand to better align with your current market position and goals.',
      details: 'We either give your brand a refreshing facelift or a complete makeover, ensuring it’s ready to dazzle in the modern market. Think of it as a brand boot camp!',
    },
  ];

  return (
    <section className="services">
      <div className="container">
        <h2 className="services-title">Our Services</h2>
        <div className="services-grid">
          {servicesList.map((service, index) => (
            <div
              key={index}
              className="service-card"
              onClick={() => handleCardClick(index)}
            >
              <div className="service-icon">{service.icon}</div>
              <h3 className="service-title">{service.title}</h3>
              <p className="service-description">{service.description}</p>
              {expandedCard === index && (
                <div className="service-details">
                  <p>{service.details}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Services;
