// src/components/Steps/Steps.js
import React from 'react';
import './Steps.css';

const stepsData = [
  {
    id: 1,
    title: 'Step 1: Initial Consultation',
    description: 'We begin with an initial consultation to understand your needs and goals.',
    imgSrc: '/path/to/sample-image1.jpg',
  },
  {
    id: 2,
    title: 'Step 2: Strategy Development',
    description: 'Our team develops a tailored strategy to achieve your objectives.',
    imgSrc: '/path/to/sample-image2.jpg',
  },
  {
    id: 3,
    title: 'Step 3: Implementation',
    description: 'We execute the plan with precision and attention to detail.',
    imgSrc: '/path/to/sample-image3.jpg',
  },
  // Add more steps as needed
];

function Steps() {
  return (
    <section className="steps-section">
      {stepsData.map((step, index) => (
        <div key={step.id} className={`step ${index % 2 === 0 ? 'left' : 'right'}`}>
          <div className="step-text">
            <h3>{step.title}</h3>
            <p>{step.description}</p>
          </div>
          <div className="step-image">
            <img src={step.imgSrc} alt={step.title} />
          </div>
        </div>
      ))}
    </section>
  );
}

export default Steps;
