// src/components/Chatbot/Chatbot.js
import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import { FaRobot, FaTimes, FaMinus } from 'react-icons/fa';

function Chatbot() {
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 20, y: 20 });
  const [startPosition, setStartPosition] = useState({ x: 0, y: 0 });
  const [isOpen, setIsOpen] = useState(false);
  const chatRef = useRef(null);
  const windowRef = useRef(null);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartPosition({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - startPosition.x,
        y: e.clientY - startPosition.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    } else {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    }

    // Cleanup on unmount
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  useEffect(() => {
    if (windowRef.current && chatRef.current) {
      const { offsetWidth, offsetHeight } = windowRef.current;
      const chatX = position.x - offsetWidth / 2 + chatRef.current.offsetWidth / 2;
      const chatY = position.y - offsetHeight - 20; // 20px for spacing

      windowRef.current.style.left = `${chatX}px`;
      windowRef.current.style.top = `${chatY}px`;
    }
  }, [isOpen, position]);

  return (
    <>
      <div
        className="chatbot-icon"
        ref={chatRef}
        style={{ left: `${position.x}px`, top: `${position.y}px` }}
        onMouseDown={handleMouseDown}
      >
        <FaRobot size={30} onClick={toggleChat} />
      </div>

      {isOpen && (
        <div className="chat-window" ref={windowRef}>
          <div className="chat-header">
            <h4>Chat with us!</h4>
            <div className="chat-controls">
              <FaTimes onClick={() => setIsOpen(false)} />
            </div>
          </div>
          <div className="chat-body">
            <p>Welcome to our chatbot! How can we help you today?</p>
            {/* You can add an input and buttons here for actual chat functionality */}
          </div>
        </div>
      )}
    </>
  );
}

export default Chatbot;
