// src/components/HeroSection/HeroSection.js
import React, { useEffect } from 'react';
import './HeroSection.css';

function HeroSection() {
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      document.querySelector('.hero').style.backgroundPosition = `center calc(50% + ${scrollPosition * 0.5}px)`;
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <section className="hero">
      <div className="hero-content">
        <h1 className="hero-title">STAND OUT</h1>
        <p className="hero-subtitle">Creating Lasting Impressions and Building Strong Connections</p>
        <div className="hero-buttons">
          <a href="#services" className="hero-button primary">Our Services</a>
          <a href="#contact" className="hero-button secondary">Contact Us</a>
        </div>
      </div>
    </section>
  );
}

export default HeroSection;
