// src/components/Consultation/Consultation.js
import React, { useState } from 'react';
import './Consultation.css';

function Consultation() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic, such as sending data to a server
    alert(`Thank you, ${name}! We will contact you soon to confirm your consultation on ${date}.`);
    // Reset form fields
    setName('');
    setEmail('');
    setDate('');
    setMessage('');
  };

  return (
    <section className="consultation">
      <div className="container">
        <h2 className="consultation-title">Book Your Consultation</h2>
        <p className="consultation-subtitle">Let's discuss how we can help you achieve your goals. Book a free consultation with our experts.</p>
        <form className="consultation-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="consultation-input"
            required
          />
          <input
            type="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="consultation-input"
            required
          />
          <input
            type="date"
            placeholder="Preferred Date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="consultation-input"
            required
          />
          <textarea
            placeholder="Your Message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="consultation-input consultation-textarea"
          ></textarea>
          <button type="submit" className="consultation-button">Book Now</button>
        </form>
      </div>
    </section>
  );
}

export default Consultation;
