// src/components/Newsletter/Newsletter.js
import React, { useState } from 'react';
import './Newsletter.css';

function Newsletter() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission (e.g., send data to your backend or an email marketing service)
    alert(`Thank you for subscribing, ${name}!`);
    setEmail('');
    setName('');
  };

  return (
    <section className="newsletter">
      <div className="container">
        <h2 className="newsletter-title">Join Our Newsletter</h2>
        <p className="newsletter-subtitle">Stay infromed!</p>
        <form className="newsletter-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="newsletter-input"
          />
          <input
            type="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="newsletter-input"
            required
          />
          <button type="submit" className="newsletter-button">Subscribe Now</button>
        </form>
      </div>
    </section>
  );
}

export default Newsletter;
